import React from "react";
import Header from '../Header';
import Main from '../Main/Main';
import Footer from '../Footer/Footer';
import PopupWithForm from '../PopupWithForm/PopupWithForm';
import ImagePopup from '../ImagePopup/ImagePopup';
import api from "../../utils/Api";
// Импортируем объект контекста
import { CurrentUserContext } from '../../contexts/CurrentUserContext';

function App() {
  // переменные состояния, отвечающие за видимость трёх попапов:
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  /* Значение переменной задаётся из обработчика handleCardClick
   и сбрасывается из обработчика closeAllPopups. */
  const [selectedCard, setSelectedCard] = React.useState(null);

  const [currentUser, setCurrentUser ] = React.useState({});

  React.useEffect(() => {
    api.getProfile()
    .then(currentUserData => {
      // console.log('currentUserData -> ', currentUserData);
      setCurrentUser(currentUserData);
    })
    .catch((err) => {console.log(err);});
  }, []);

  // console.log('currentUser -> ', currentUser);

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  // обработчик пропса onClose компонента PopupWithForm
  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard(null);
  }

  function handleCardClick(card) {
    setSelectedCard(card);
  }

  return (
    // «Внедряем» данные из currentUser с помощью провайдера контекста
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        <Header />
        <Main
          // Прокидываем пропсы в компонент Main
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
          onCardClick={handleCardClick}
        />

        <Footer />

      {/* Форма редактирования профиля («Редактировать профиль») */}
      <PopupWithForm
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
        name="profile_edit"
        title="Редактировать профиль"
        buttonTitle="Сохранить">
        <label htmlFor="el-name-input" className="popup__field-wrapper">
          <input type="text" name="name" id="el-name-input" className="popup__field popup__field_el_name" placeholder="Имя" required minLength="2" maxLength="40" />
          <span className="el-name-input-error popup__field-error"></span>
        </label>
        <label htmlFor="el-description-input" className="popup__field-wrapper">
          <input type="text" name="description" id="el-description-input" className="popup__field popup__field_el_description" placeholder="О себе" required minLength="2" maxLength="200" />
          <span className="el-description-input-error popup__field-error"> </span>
        </label>
      </PopupWithForm>

      {/* Форма добавления карточки («Новое место»)*/}
      <PopupWithForm isOpen={isAddPlacePopupOpen} onClose={closeAllPopups} name="card_add" title="Новое место" buttonTitle="Создать">
        <label htmlFor="card-name-input" className="popup__field-wrapper">
          <input type="text" name="name" id="card-name-input" className="popup__field popup__field_card_name" placeholder="Название" required minLength="2" maxLength="30" />
          <span className="popup__field-error card-name-input-error"></span>
        </label>
        <label htmlFor="image-link-input" className="popup__field-wrapper">
          <input type="url" name="link" id="image-link-input" className="popup__field popup__field_card_link" placeholder="Ссылка на картинку" required />
          <span className="popup__field-error image-link-input-error"></span>
        </label>
      </PopupWithForm>

      {/* Форма удаления карточки («Вы уверены?»)*/}
      <PopupWithForm name="card_delete" title="Вы уверены?" buttonTitle="Да" />

      {/* Форма обновления аватара («Обновить аватар»)*/}
      <PopupWithForm isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} name="avatar_update" title="Обновить аватар" buttonTitle="Сохранить">
        <label htmlFor="image-link-input" className="popup__field-wrapper">
          <input type="url" name="link" id="avatar-link-input" className="popup__field popup__field_card_link" placeholder="Ссылка аватар" required />
          <span className="popup__field-error avatar-link-input-error"></span>
        </label>
      </PopupWithForm>
      {/* Значение selectedCard должно передаваться с помощью пропса card в компонент ImagePopup */}


      <ImagePopup
        card={selectedCard}
        onClose={closeAllPopups}
      />
      </div>
      </CurrentUserContext.Provider>
  );
}

export default App;
