import React from "react";
import api from '../../utils/Api'
import Card from '../Card/Card';
import { CurrentUserContext } from '../../contexts/CurrentUserContext';

function Main({
  onEditProfile,
  onAddPlace,
  onEditAvatar,
  onCardClick,
  cards
}) {
  //переменные состояния отвечающие за данные пользователя
  const currentUser = React.useContext(CurrentUserContext);
  // const [userName, setUserName] = React.useState("Жак-Ив Кусто");
  // const [userDescription, setUserDescription] = React.useState("Исследователь океана");
  // const [userAvatar, setUserAvatar] = React.useState(null);
  // переменная состояния карточек
  // const [cards, setCards] = React.useState([]);
  console.log('cards -> ', cards)
  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some(i => i._id === currentUser._id);
/*     console.log("handleCardLike isLiked", isLiked )
    console.log("card ->", card ) */
  // Отправляем запрос в API и получаем обновлённые данные карточки
  /*   api.changeLikeCardStatus(card._id, !isLiked)
      .then((newCard) => {
        setCards((state) =>
          state.map((c) => c._id === card._id ? newCard : c));
    }); */

    if (!isLiked){
      api.addLike(card._id)
      .then((newCard) => {
        setCards((state) =>
          state.map((c) => (c._id === card._id ? newCard : c)));
    }).catch((err) => {
      console.log(err);
    });
    } else {
      api.deleteLike(card._id)
        .then((newCard) => {
          setCards((state) =>
            state.map((c) =>( c._id === card._id ? newCard : c)));
      }).catch((err) => {
        console.log(err);
      });
    }
}
  // запрос в API за пользовательскими данными.
/*   React.useEffect(() => {
    api.getProfile()
      .then(res => {
        const userData = res;
        setUserName(userData.name);
        setUserDescription(userData.about);
        setUserAvatar(userData.avatar);
      })
      .catch((err) => {
        console.log(err);
      }, []);
  }) */
// удаление карточки
  function handleCardDelete(card){
    const isOwn = card.owner._id === currentUser._id;
    // console.log('isOwn -> ', isOwn);
    if (isOwn){
      api.deleteCard(card._id)
      .then(() => {
        setCards((state) => state.filter((c) => c._id !== card._id));
      })
      .catch((err) => {
        console.log(err);
      });
    }

  }

  // запрос к API за данными карточек
  React.useEffect(() => {
    api.getCards()
      .then(res => {
        // console.log('getCards -> ', res)
        const cardsData = res.map(item => {
          return {
            name: item.name,
            link: item.link,
            _id: item._id,
            likes: item.likes,
            owner: item.owner
          }
        });
        // Сохраняем карточки в стейт cards
        setCards(cardsData);
      })
      .catch((err) => {
        console.log(err);
      });
  },[])

  return (<main className="content">
    <section className="profile">
      <div className="profile__data">
        {currentUser.avatar && <div className="profile__avatar" style={{ backgroundImage: `url(${currentUser.avatar})` }}  onClick={onEditAvatar}></div>}

        <div className="profile__info">
          <div className="profile__edit">
            <h1 className="profile__title">{currentUser.name}</h1>
            <button type="button" className="profile__edit-button" aria-label="Редактировать" onClick={onEditProfile}></button>
          </div>
          <p className="profile__text">{currentUser.about}</p>
        </div>
      </div>
      <button type="button" className="profile__add-button" onClick={onAddPlace}></button>
    </section>

    <section className="elements">
      {cards.map(card => (

        <Card
          key={card._id}
          onCardClick={onCardClick}
          card={card}
          name={card.name}
          likes={card.likes}
          owner={card.owner}
          onCardLike={handleCardLike}
          onCardDelete={handleCardDelete }
        />
      ))}
    </section>

  </main>)
}

export default Main;
