import React from "react";
import api from '../../utils/Api'
import Card from '../Card/Card';
import { CurrentUserContext } from '../../contexts/CurrentUserContext';

function Main({
  onEditProfile,
  onAddPlace,
  onEditAvatar,
  onCardClick,
}) {
  //переменные состояния отвечающие за данные пользователя
  const currentUser = React.useContext(CurrentUserContext);
  const [userName, setUserName] = React.useState("Жак-Ив Кусто");
  const [userDescription, setUserDescription] = React.useState("Исследователь океана");
  const [userAvatar, setUserAvatar] = React.useState(null);
  // переменная состояния карточек
  const [cards, setCards] = React.useState([]);

  // запрос в API за пользовательскими данными.
  React.useEffect(() => {
    api.getProfile()
      .then(res => {
        const userData = res;
        setUserName(userData.name);
        setUserDescription(userData.about);
        setUserAvatar(userData.avatar);
      })
      .catch((err) => {
        console.log(err);
      }, []);
  })
  // запрос к API за данными карточек
  React.useEffect(() => {
    api.getCards()
      .then(res => {
        const cardsData = res.map(item => {
          return {
            name: item.name,
            link: item.link,
            id: item._id,
            likes: item.likes
          }
        });
        // Сохраняем карточки в стейт cards
        setCards(cardsData);
      })
      .catch((err) => {
        console.log(err);
      });
  },[])

  return (<main className="content">
    <section className="profile">
      <div className="profile__data">
        {userAvatar && <div className="profile__avatar" style={{ backgroundImage: `url(${currentUser.avatar})` }} onClick={onEditAvatar}></div>}
        <div className="profile__info">
          <div className="profile__edit">
            <h1 className="profile__title">{currentUser.name}</h1>
            <button type="button" className="profile__edit-button" aria-label="Редактировать" onClick={onEditProfile}></button>
          </div>
          <p className="profile__text">{userDescription}</p>
        </div>
      </div>
      <button type="button" className="profile__add-button" onClick={onAddPlace}></button>
    </section>

    <section className="elements">
      {cards.map(card => (

        <Card
          key={card.id}
          onCardClick={onCardClick}
          card={card}
          name={card.name}
          likes={card.likes.length}
        />
      ))}
    </section>

  </main>)
}

export default Main;
