function Main(){
  return (<main className ="content">
  <section className ="profile">
    <div className ="profile__data">
    <div className ="profile__avatar" onClick={handleEditAvatarClick}></div>
      <div className ="profile__info">
        <div className ="profile__edit">
          <h1 className ="profile__title">Жак-Ив Кусто</h1>
          <button type="button" className ="profile__edit-button" aria-label="Редактировать" onClick={handleEditProfileClick}></button>
        </div>
        <p className ="profile__text">Исследователь океана</p>
      </div>
    </div>
    <button type="button" className ="profile__add-button" onClick={handleAddPlaceClick}></button>
  </section>

  <section className ="elements">
  </section>
</main>)
}
// document.querySelector('.profile__avatar').addEventListener('click', () => {handleEditAvatarClick});
function handleEditAvatarClick(){
  console.log("handleEditAvatarClick -->")
  document.querySelector('.popup_type_avatar_update').classList.add('popup_opened');
}

function handleEditProfileClick(){
  console.log("handleEditProfileClick -->");
  document.querySelector('.popup_type_profile_edit').classList.add('popup_opened');
}
function handleAddPlaceClick(){
  console.log("handleAddPlaceClick -->")
  document.querySelector('.popup_type_card_add').classList.add('popup_opened');
}
export default Main;
