import React from "react";
import { Route, Switch, Link } from "react-router-dom";
import Header from '../Header';
import Login from '../Login/Login';
import Register from '../Register/Register';
import Main from '../Main/Main';
import Footer from '../Footer/Footer';
import PopupWithForm from '../PopupWithForm/PopupWithForm';
import ImagePopup from '../ImagePopup/ImagePopup';
import api from "../../utils/Api";
import EditProfilePopup from "../EditProfilePopup/EditProfilePopup";
import EditAvatarPopup from "../EditAvatarPopup/EditAvatarPopup";
import AddPlacePopup from '../AddPlacePopup/AddPlacePopup';
// Импортируем объект контекста
import { CurrentUserContext } from '../../contexts/CurrentUserContext';

function App() {
  // переменные состояния, отвечающие за видимость трёх попапов:
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  /* Значение переменной задаётся из обработчика handleCardClick
   и сбрасывается из обработчика closeAllPopups. */
  const [selectedCard, setSelectedCard] = React.useState(null);
  // Контекст текущего пользователя
  const [currentUser, setCurrentUser] = React.useState({});
  const [cards, setCards] = React.useState([]);
  // запрос к API за данными карточек
  React.useEffect(() => {
    api.getCards()
      .then(res => {
        // console.log('getCards -> ', res)
        const cardsData = res.map(item => {
          return {
            name: item.name,
            link: item.link,
            _id: item._id,
            likes: item.likes,
            owner: item.owner
          }
        });
        // Сохраняем карточки в стейт cards
        setCards(cardsData);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [])
  // добавление карточки
  function handleAddPlaceSubmit({ name, link }) {
    api.addCard({ name, link })
      .then((newCard) => {
        setCards([newCard, ...cards]);
        closeAllPopups();
      })
      .catch((err) => {
        console.log(err);
      });
  }
  // установка- снятие лайка
  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    api.changeLikeCardStatus(card._id, isLiked)
      .then((newCard) => {
        setCards((state) =>
          state.map((c) => c._id === card._id ? newCard : c));
      })
      .catch((err) => {
        console.log(err);
      });
  }
  // удаление карточки
  function handleCardDelete(card) {
    const isOwn = card.owner._id === currentUser._id;
    if (isOwn) {
      api.deleteCard(card._id)
        .then(() => {
          setCards((state) => state.filter((c) => c._id !== card._id));
        })
        .catch((err) => {
          console.log(err);
        });
    }
  }
  // запрос к API за данными пользователя
  React.useEffect(() => {
    api.getProfile()
      .then(currentUserData => {
        // console.log('currentUserData -> ', currentUserData);
        setCurrentUser(currentUserData);
      })
      .catch((err) => { console.log(err); });
  }, []);

  // обработчик редактирования профиля
  function handleUpdateUser({ name, about }) {
    api.setUserInfo({ name, about })
      .then(currentUserData => {
        setCurrentUser(currentUserData);
        closeAllPopups();
      })
      .catch((err) => { console.log(err); });
  }

  function handleUpdateAvatar(avatar) {
    api.setAvatar(avatar)
      .then(currentUserData => {
        // console.log('currentUserData -> ', currentUserData);
        setCurrentUser(currentUserData);
        closeAllPopups();
      })
      .catch((err) => { console.log(err); });
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  // обработчик пропса onClose компонента PopupWithForm
  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard(null);
  }

  function handleCardClick(card) {
    setSelectedCard(card);
  }

  return (
    // «Внедряем» данные из currentUser с помощью провайдера контекста
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        <Header />
        <Switch>
          <Route path="/signin">
            <Login />
          </Route>
          <Route path="/signup">
            <Register />
          </Route>
          <Route path="/">
            <Main
            // Прокидываем пропсы в компонент Main
              cards={cards}
              onEditProfile={handleEditProfileClick}
              onAddPlace={handleAddPlaceClick}
              onEditAvatar={handleEditAvatarClick}
              onCardClick={handleCardClick}
              onCardLike={handleCardLike}
              onCardDelete={handleCardDelete}
            />
          </Route>

        </Switch>

        <Footer />

        {/* Форма редактирования профиля («Редактировать профиль») */}
        <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} onUpdateUser={handleUpdateUser} />

        {/* Форма добавления карточки («Новое место»)*/}
        <AddPlacePopup
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          onAddPlace={handleAddPlaceSubmit}
        />

        {/* Форма удаления карточки («Вы уверены?»)*/}
        <PopupWithForm name="card_delete" title="Вы уверены?" buttonTitle="Да" />

        {/* Форма обновления аватара («Обновить аватар»)*/}
        <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} onUpdateAvatar={handleUpdateAvatar} />

        {/* Значение selectedCard должно передаваться с помощью пропса card в компонент ImagePopup */}
        <ImagePopup
          card={selectedCard}
          onClose={closeAllPopups}
        />

      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
