import React, { useState } from "react";
import { Link, withRouter } from 'react-router-dom';
import * as mestoAuth from '../../mestoAuth';

const Login = (props) => {
  const [inputs, setInputs] = useState({
    email: '',
    password: '',
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInputs((prev) => ({
      ...prev,
      [name]: value,
    }));
  }
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!inputs.email || !inputs.password) {
      return;
    }
    props.handleLogin(inputs.email, inputs.password)
      .catch(setMessage);
    /*  mestoAuth.authorize(inputs.email, inputs.password)
     .then((data) => {
       // нужно проверить, есть ли у данных jwt
       if(data.jwt){
        // сбросьте стейт, затем в колбэке установите
       setInputs({
       email: '',
       password: ''
     }), () => {
        // стейт loggedIn родительского App как true,
       props.handleLogin();
       // затем перенаправьте его в /
       props.history.push('/');
     }}
   })
   .catch(err => console.log(err)) */
  }
  return (
    <section className="register">
      <h2 className="register__title">Вход</h2>
      <form
        className="register__form"
        onSubmit={handleSubmit}
        >
        <input
          type="email"
          className="register__input"
          name="email"
          id="email"
          required
          minLength="2"
          maxLength="20"
          placeholder="Email"
        />
        <input
          type="password"
          className="register__input"
          name="password"
          id="password"
          required
          minLength="2"
          maxLength="20"
          placeholder="Пароль"
        />
        <button
          className="register__submit"
          type="submit"
        >Войти</button>
      </form>
    </section>
  )
}

export default Login;
