import React from "react";
import api from '../../utils/Api'
import Card from "../Card/card";
function Main({
  onEditProfile,
  onAddPlace,
  onEditAvatar
}){
  //переменные состояния userName, userDescription и userAvatar отвечающие за данные пользователя
  const [userName, setUserName] = React.useState("Жак-Ив Кусто");
  const [userDescription, setUserDescription] = React.useState("Исследователь океана");
  const [userAvatar, setUserAvatar] = React.useState();
  // переменная стейта cards с пустым массивом в качестве значения по умолчанию.
  const [cards, setCards] = React.useState([]);
 // console.log("cards ->", cards);
  // эффект, вызываемый при монтировании компонента,
  // который будет совершать запрос в API за пользовательскими данными.
  // После получения ответа задавайте полученные данные в соответствующие переменные состояния.
  React.useEffect(() => {
    api.getProfile()
      .then(res => {
        // console.log("Данные пользователя с сервера -> ", res );
        const userData = res;
        setUserName(userData.name);
        setUserDescription(userData.about);
        setUserAvatar(userData.avatar);
      })
      .catch((err) => {
        console.log(err);
      });
  })
  // запрос к API за данными
  React.useEffect(() => {
    api.getCards()
    .then(res => {
      const cardsData = res.map(item => {
        return {
          name: item.name,
          link: item.link,
          id: item._id,
          likes: item.likes
        }
      },[]);
    // Сохраняем карточки в стейт cards
      setCards(cardsData);
    // console.log('resCards -> ', res);
      // console.log("cards ->", cards);

    })
  })


  return (<main className ="content">
  <section className ="profile">
    <div className ="profile__data">
    <div className ="profile__avatar" style={{backgroundImage: `url(${userAvatar})`}} onClick={onEditAvatar}></div>
      <div className ="profile__info">
        <div className ="profile__edit">
          <h1 className ="profile__title">{userName}</h1>
          <button type="button" className ="profile__edit-button" aria-label="Редактировать" onClick={onEditProfile}></button>
        </div>
        <p className ="profile__text">{userDescription}</p>
      </div>
    </div>
    <button type="button" className ="profile__add-button" onClick={onAddPlace}></button>
  </section>

  <section className ="elements">
 { cards.map(item => (
   <Card style={{ backgroundImage: `url(${item.link})` }} name={item.name} likes={item.likes.length}/>
   /*
    {<article className="element">
      <button type="button" className="element__delete-button"></button>
      <div className="element__image" style={{ backgroundImage: `url(${item.link})` }}></div>
      <div className="element__heading">
        <h2 className="element__heading-text">{item.name}</h2>
        <div className="element__like-wrap">
          <button type="button" className="element__like-button"></button>
          <span className="element__like-count">{item.likes.length}</span>
        </div>
      </div>
    </article> }*/
  ))}
  </section>

</main>)
}


export default Main;
