import React from "react";

function PopupWithForm(props){
  const [searchText, setSearchText] = React.useState('Start!');
  console.log(searchText);
  const handleFormSubmit = (e) => {
    e.preventDefault();
    console.log('Bla ->', e);
  };
  return (
    <div className ={`popup popup_type_${props.name} ${props.isOpen ? 'popup_opened' : ''}`} >
        <div className ="popup__container">
        {/* закрытие попапов. пропс onClose */}
          <button type="button" className ="popup__close" onClick={props.onClose}></button>
          <h2 className ="popup__heading">{props.title}</h2>
          <form className ="popup__main-container" name={props.name} noValidate onSubmit={handleFormSubmit}>
            {props.children}
            <button type="submit" className ="popup__submit" >{props.buttonTitle}</button>
          </form>

        </div>
    </div>


  );
}

export default PopupWithForm;
