import React from "react";
import Header from '../Header/Header';
import Main from '../Main/Main';
import Footer from '../Footer/Footer'
import api from '../../api/Api';
import PopupWithForm from '../PopupWithForm/PopupWithForm';
import ImagePopup from '../ImagePopup/ImagePopup'
// import './App.css';


function App() {
  // переменные состояния, отвечающие за видимость трёх попапов:
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);

  // перенесите добавленные обработчики событий из компонента Main в компонент App
  function handleEditAvatarClick(){
    console.log("handleEditAvatarClick -->")
    setIsEditAvatarPopupOpen(true);
    // document.querySelector('.popup_type_avatar_update').classList.add('popup_opened');
  }

  function handleEditProfileClick(){
    console.log("handleEditProfileClick -->");
    setIsEditProfilePopupOpen(true);
    // document.querySelector('.popup_type_profile_edit').classList.add('popup_opened');
  }
  function handleAddPlaceClick(){
    console.log("handleAddPlaceClick -->")
    setIsAddPlacePopupOpen(true);
    // document.querySelector('.popup_type_card_add').classList.add('popup_opened');
  }
  function closeAllPopups(){
    console.log('closeAllPopups -->');
    setIsEditProfilePopupOpen(false);

  }
  return (
    <div className="App">
      <div className ="page">
    <Header />
    <Main
    // При этом, чтобы они продолжали вызываться из компонента Main,
    // передавайте их в последний с помощью новых пропсов
      onEditProfile = {handleEditProfileClick}
      onAddPlace = {handleAddPlaceClick}
      onEditAvatar = {handleEditAvatarClick}
      // onClose = {closeAllPopups}
    />
    <Footer />
  </div>
  {/* Форма редактирования профиля («Редактировать профиль») */}
  <PopupWithForm isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} name="profile_edit" title="Редактировать профиль" buttonTitle="Сохранить">
    <label htmlFor="el-name-input" className ="popup__field-wrapper">
      <input type="text" name="name" id="el-name-input" className ="popup__field popup__field_el_name" placeholder="Имя" required minLength="2" maxLength="40" />
      <span className ="el-name-input-error popup__field-error"></span>
    </label>
    <label htmlFor="el-description-input" className ="popup__field-wrapper">
      <input type="text" name="description" id="el-description-input" className ="popup__field popup__field_el_description" placeholder="О себе" required minLength="2" maxLength="200" />
      <span className ="el-description-input-error popup__field-error"> </span>
    </label>
  </PopupWithForm>

    {/* <div className ="popup popup_type_edit">
    <div className ="popup__container">
      <button type="button" className ="popup__close"></button>
      <h2 className ="popup__heading">Редактировать профиль</h2>
        <form className ="popup__main-container" name="profile_edit" noValidate> */}
          {/* <label htmlFor="el-name-input" className ="popup__field-wrapper">
            <input type="text" name="name" id="el-name-input" className ="popup__field popup__field_el_name" placeholder="Имя" required minLength="2" maxLength="40" />
            <span className ="el-name-input-error popup__field-error"></span>
          </label>
          <label htmlFor="el-description-input" className ="popup__field-wrapper">
            <input type="text" name="description" id="el-description-input" className ="popup__field popup__field_el_description" placeholder="О себе" required minLength="2" maxLength="200" />
            <span className ="el-description-input-error popup__field-error"> </span>
          </label> */}
          {/* <button type="submit" className ="popup__submit">Сохранить</button> */}
        {/* </form>
    </div>
  </div> */}
  {/* Форма добавления карточки («Новое место»)*/}
  <PopupWithForm isOpen={isAddPlacePopupOpen} name="card_add" title="Новое место" buttonTitle="Создать">
    <label htmlFor="card-name-input" className ="popup__field-wrapper">
      <input type="text" name="name" id="card-name-input" className ="popup__field popup__field_card_name" placeholder="Название" required minLength="2" maxLength="30" />
      <span className ="popup__field-error card-name-input-error"></span>
    </label>
    <label htmlFor="image-link-input" className ="popup__field-wrapper">
      <input type="url" name="link" id="image-link-input" className ="popup__field popup__field_card_link" placeholder="Ссылка на картинку" required />
      <span className ="popup__field-error image-link-input-error"></span>
    </label>
  </PopupWithForm>

  {/* <div className ="popup popup_type_card_add">
    <div className ="popup__container">
      <button type="button" className ="popup__close"></button>
      <h2 className ="popup__heading">Новое место</h2>
        <form className ="popup__main-container" name="card_add" noValidate> */}
         {/*  <label htmlFor="card-name-input" className ="popup__field-wrapper">
            <input type="text" name="name" id="card-name-input" className ="popup__field popup__field_card_name" placeholder="Название" required minLength="2" maxLength="30" />
            <span className ="popup__field-error card-name-input-error"></span>
          </label>
          <label htmlFor="image-link-input" className ="popup__field-wrapper">
            <input type="url" name="link" id="image-link-input" className ="popup__field popup__field_card_link" placeholder="Ссылка на картинку" required />
            <span className ="popup__field-error image-link-input-error"></span>
          </label> */}
        {/*  <button type="submit" className ="popup__submit">Создать</button>
        </form>
    </div>
  </div> */}
   {/* Форма удаления карточки («Вы уверены?»)*/}
   <PopupWithForm name="card_delete" title="Вы уверены?" buttonTitle="Да" />
 {/*  <div className ="popup popup_type_delete-confirm">
    <div className ="popup__container popup__container_type_delete-confirm">
      <button type="button" className ="popup__close"></button>
      <h2 className ="popup__heading">Вы уверены?</h2>
        <form className ="popup__main-container" name="card_delete" noValidate>
          <button type="submit" className ="popup__submit popup__submit_type_delete-confirm">Да</button>
        </form>
    </div>
  </div> */}
  {/* Форма обновления аватара («Обновить аватар»)*/}
  <PopupWithForm isOpen={isEditAvatarPopupOpen} name="avatar_update" title="Обновить аватар" buttonTitle="Сохранить">
    <label htmlFor="image-link-input" className ="popup__field-wrapper">
      <input type="url" name="link" id="avatar-link-input" className ="popup__field popup__field_card_link" placeholder="Ссылка аватар" required />
      <span className ="popup__field-error avatar-link-input-error"></span>
    </label>
  </PopupWithForm>
  <ImagePopup />
  {/* <div className ="popup popup_type_avatar">
    <div className ="popup__container popup__container_avatar">
      <button type="button" className ="popup__close"></button>
      <h2 className ="popup__heading">Обновить аватар</h2>
        <form className ="popup__main-container" name="avatar_update" noValidate>
          <label htmlFor="image-link-input" className ="popup__field-wrapper">
            <input type="url" name="link" id="avatar-link-input" className ="popup__field popup__field_card_link" placeholder="Ссылка аватар" required />
            <span className ="popup__field-error avatar-link-input-error"></span>
          </label>
          <button type="submit" className ="popup__submit popup__submit_avatar">Сохранить</button>
        </form>
    </div>
  </div> */}

   {/* Шаблон карточки */}
  <template className ="element-template">
    <article className ="element">
      <button type="button" className ="element__delete-button"></button>
      <div className ="element__image"></div>
      {/* img src="./images/karachaevsk.jpg" alt="фото места" className ="element__image" */}
      <div className ="element__heading">
        <h2 className ="element__heading-text"></h2>
        <div className ="element__like-wrap">
          <button type="button" className ="element__like-button"></button>
          <span className ="element__like-count"></span>
        </div>

      </div>
    </article>
  </template>
  </div>
  );
}

export default App;
